mapper.select:
SELECT member_id
FROM member
WHERE member_no = #{memberNo}

mapper.update:
UPDATE qna
SET qna_answer = #{qnaAnswer}
WHERE qna_no = #{qnaNo}

UPDATE qna
SET qna_answer = NULL
WHERE qna_no = #{qnaNo}

mapper.select:
SELECT *
FROM qna
WHERE item_no = #{itemNo}
ORDER BY qna_no DESC
LIMIT #{startPage}, #{pagePerMember}

SELECT *
FROM qna
WHERE member_no = #{memberNo}
ORDER BY qna_answer is null, qna_no DESC
LIMIT #{startPage}, #{pagePerMember}

mapper.update:
UPDATE qna
SET qna_title = #{qnaTitle},
    qna_Classification = #{qnaClassification},
    qna_Question = #{qnaQuestion}
WHERE qna_no = #{qnaNo}
